import React from 'react';
import Parse from "parse";
import { Map } from "immutable";
import { Table, Divider, Button, Input, Icon, Row, Col } from 'antd';
import tableController from './tableController';
import OprandView from '../oprand/OprandView';


const fields = Map({
  name: "string",
  id: "number",
  date: "time",
  boolean: "boolean"
});

const constraints = Map({
  string: [
    "Contains",
    "StartsWith",
    "EndsWith",
    "EqualTo",
    "NotEqualTo",
    "GreaterThan",
    "LessThan",
    "GreaterThanOrEqualTo",
    "LessThanOrEqualTo",
    "IsEmpty",
    "NotIsEmpty",
    "IsNull",
    "NotIsNull"
  ],
  number: [
    "EqualTo",
    "NotEqualTo",
    "GreaterThan",
    "LessThan",
    "GreaterThanOrEqualTo",
    "LessThanOrEqualTo",
    "IsNull",
    "NotIsNull"
  ],
  time: [
    "EqualTo",
    "NotEqualTo",
    "GreaterThan",
    "LessThan",
    "GreaterThanOrEqualTo",
    "LessThanOrEqualTo",
    "IsNull",
    "NotIsNull"
  ],
  boolean: [],
  default: [
    "EqualTo",
    "NotEqualTo",
    "GreaterThan",
    "LessThan",
    "GreaterThanOrEqualTo",
    "LessThanOrEqualTo",
    "IsNull",
    "NotIsNull"
  ]
});


const TableView = (props) => {
  const {
    data,
    addPerson,
    setPersonName,
    deletePerson,
    editPerson,
    confirmEdit,
  } = tableController();

  const { filterData } = props
  const columns = [
    {
      title: 'objectId',
      dataIndex: 'objectId',
      key: 'objectId',
    },
    {
      title: 'Name',
      dataIndex: 'name',
      key: 'name',
      render: text => <a>{text}</a>,
    },
    {
      title: 'createdAt',
      dataIndex: 'createdAt',
      key: 'createdAt',
    },
    {
      title: 'updatedAt',
      dataIndex: 'updatedAt',
      key: 'updatedAt',
    },
    {
      title: 'Action',
      key: 'action',
      render: (text, record) => (
        <span>
          <Icon type="delete" key="delete" onClick={() => deletePerson(record.key)}></Icon>
          <Divider type="vertical" />
          <Icon type="edit" key="edit" onClick={() => editPerson(record.key)}></Icon>
        </span>
      ),
    },
  ];


  let tmpdata;
  let dataSource;
  const ClouseQuery = Parse.Object.extend("ClouseQuery");
  // const mainQuery = new Parse.Query(ClouseQuery);

  const makeMainQuery = (oprandData) => {
    let mainQuery = new Parse.Query(ClouseQuery);
    if (oprandData.op === "and") {
      oprandData.childs.map(item => {
        if (!item.childs) {
          mainQuery = Parse.Query.and(mainQuery, item);
        } else {
          mainQuery = Parse.Query.and(mainQuery, item.mainQuery);
        }
      });
    } else {
      oprandData.childs.map(item => {
        if (!item.childs) {
          mainQuery = Parse.Query.or(mainQuery, item);
        } else {
          mainQuery = Parse.Query.or(mainQuery, item.mainQuery);
        }
      });
    }
    // filterData.mainQuery = mainQuery;
    tmpdata = oprandData;
    mainQuery.find().then(result => {
      filterData.searchResults = result
      console.log(result)
      dataSource = result.map((item, key) => {
        return {
          ...item.toJSON(),
          key,
        }
      })
      console.log(dataSource)
    });

  };



  return (
    <div style={{ padding: 24 }}>
      <Button onClick={() => makeMainQuery(tmpdata)}>Search</Button>
      <OprandView
        makeMainQuery={makeMainQuery}
        filterData={filterData}
        constraints={constraints}
        fields={fields}
      />

      <Row>
        <Col span={8}>
          {data.editing &&
            <h1>Edit Mode</h1>
          }
          <Input placeholder="name" value={data.personName} onChange={(e) => setPersonName(e.target.value)} />
        </Col>
        <Col span={8}>
          {!data.editing &&
            <Button onClick={() => addPerson()}>add</Button>
          }
          {data.editing &&
            <Button onClick={() => confirmEdit()}>confirm edit</Button>
          }
        </Col>
      </Row>
      <Row>
        <Col span={12}>
          <Table style={{ padding: 24 }} columns={columns} dataSource={dataSource} />
        </Col>
      </Row>
    </div>
  );
};

export default TableView;
