import Parse from 'parse';
import {
  Record,
} from 'immutable';
import {
  withState, withHandlers, pipe,
} from '../../util';


const ClouseQuery = Parse.Object.extend('ClouseQuery');


const init = (props) => props.filterData;

const addClouse = ({ setData }) => () => {
  const query = new Parse.Query(ClouseQuery);
  setData((d) => d.set('childs', d.childs.concat(query)));
};

const deleteChild = ({ setData }) => (index) => {
  setData((d) => d.set('childs', d.childs.filter((value, i) => i !== index)));
};

const changeOprand = ({ setData }) => (oprand) => {
  setData((d) => d.set('op', oprand));
};

const addOprand = ({ setData }) => () => {
  setData((d) => d.set('childs', d.childs.concat(Record({ op: 'and', childs: [] })())));
};

const makeMainQuery = ({ data }) => {
  console.log('maake main query start', data);
  if (data.child) {
    const { op } = data;
    data.child.map((x) => {
      if (op === 'and') { return data.mainQuery = Parse.Query.and(data.mainQuery, makeMainQuery(x)); }
      if (op === 'or') {
        console.log('op', data.op);
        return data.mainQuery = Parse.Query.or(data.mainQuery, makeMainQuery(x));
      }
      if (!x.op) {
        if (op === 'and') {
          console.log('op', op);
          data.mainQuery = Parse.Query.and(data.mainQuery, x);
        } else {
          data.mainQuery = Parse.Query.or(data.mainQuery, x);
        }
      } else {
        return makeMainQuery(x);
      }
    });
  } else {
    // console.log('tt',treeData)
    return data;
  }
};


const oprandController = pipe(
  withState(init),
  withHandlers({
    addClouse,
    deleteChild,
    changeOprand,
    addOprand,
  }),
);

export default oprandController;
