import Parse from 'parse';
import {
  withState, withHandlers, pipe,
} from '../../util';


const ClouseQuery = Parse.Object.extend('ClouseQuery');

const init = (props) => props.filterData;

const makeMainQuery = ({ setData, newd }) => {
  let mainQuery = new Parse.Query(ClouseQuery);
  if (newd.op === 'and') {
    newd.childs.map((item) => {
      if (!item.childs) { mainQuery = Parse.Query.and(mainQuery, item); } else {
        mainQuery = Parse.Query.and(mainQuery, item.mainQuery);
      }
    });
  } else {
    newd.childs.map((item) => {
      if (!item.childs) { mainQuery = Parse.Query.or(mainQuery, item); } else {
        mainQuery = Parse.Query.and(mainQuery, item.mainQuery);
      }
    });
  }
  newd.mainQuery = mainQuery;
};

const addClouse = ({ setData, data }) => () => {
  const query = new Parse.Query(ClouseQuery);
  const newd = { ...data };
  newd.childs = newd.childs.concat(query);
  setData(newd);

  makeMainQuery({ setData, newd });
};

const deleteChild = ({ setData, data }) => (index) => {
  const newd = { ...data };
  newd.childs = newd.childs.filter((value, i) => i !== index);
  setData(newd);
};

const changeOprand = ({ setData, data }) => (oprand) => {
  const newd = { ...data };
  newd.op = oprand;
  setData(newd);
  makeMainQuery({ setData, newd });

  // setData((d) => d.set('op', oprand));
};

const addOprand = ({ setData, data }) => () => {
  const query = new Parse.Query(ClouseQuery);
  const newd = { ...data };
  newd.childs = newd.childs.concat({
    op: 'and', childs: [], mainQuery: query, searchResults: [],
  });
  setData(newd);
  makeMainQuery({ setData, newd });
  // setData((d) => d.set('childs', d.childs.concat(Record({ op: 'and', childs: [], mainQuery: query })())));
};


const addObject = () => () => {
  const obj = new Parse.Object('ClouseQuery');
  obj.save({
    name: 'rox',
    number: 456,

  });
};


const oprandController = pipe(
  withState(init),
  withHandlers({
    makeMainQuery,
    addClouse,
    deleteChild,
    changeOprand,
    addOprand,
    addObject,
  }),

);

export default oprandController;
