import Parse from 'parse';
import {
  Record,
} from 'immutable';
import {
  withState, withHandlers, pipe,
} from '../../util';


const ClouseQuery = Parse.Object.extend('ClouseQuery');

const init = (props) => props.filterData;

const addClouse = ({ setData, data }) => () => {
  const query = new Parse.Query(ClouseQuery);
  const newd = { ...data };
  newd.childs = newd.childs.concat(query);
  setData(newd);
};

const deleteChild = ({ setData, data }) => (index) => {
  const newd = { ...data };
  newd.childs = newd.childs.filter((value, i) => i !== index);
  setData(newd);
};

const changeOprand = ({ setData }) => (oprand) => {
  setData((d) => d.set('op', oprand));
};

const addOprand = ({ setData, data }) => () => {
  const query = new Parse.Query(ClouseQuery);
  const newd = { ...data };
  newd.childs = newd.childs.concat({ op: 'and', childs: [], mainQuery: query });
  setData(newd);
  // setData((d) => d.set('childs', d.childs.concat(Record({ op: 'and', childs: [], mainQuery: query })())));
};

let mainQuery = new Parse.Query(ClouseQuery);

const makeMainQuery = (data) => {
  console.log('maake main query start', mainQuery);
  console.log(data.mainQuery);

  if (data.childs) {
    const { op } = data;
    data.childs.map((x) => {
      if (op === 'and') {
        data.mainQuery = Parse.Query.and(data.mainQuery, makeMainQuery(x));
      }
      if (op === 'or') {
        console.log('op', data.op);
        data.mainQuery = Parse.Query.or(data.mainQuery, makeMainQuery(x));
      }
      if (!x.op) {
        if (op === 'and') {
          console.log('op', op);
          mainQuery = Parse.Query.and(mainQuery, x);
        } else {
          mainQuery = Parse.Query.or(mainQuery, x);
        }
      }
      return makeMainQuery(x);
    });
  } else {
    console.log('nochilds');
    return data;
  }
};


const recursiveFunc = () => (n) => {
  makeMainQuery(n);
};

const oprandController = pipe(
  withState(init),
  withHandlers({
    addClouse,
    deleteChild,
    changeOprand,
    addOprand,
    makeMainQuery,
    recursiveFunc,
  }),
);

export default oprandController;
