import React from 'react';
import Parse from "parse";
import { Map } from "immutable";
import { Table, Divider, Button, Input, Icon, Row, Col } from 'antd';
import tableController from './tableController';
import OprandView from '../oprand/OprandView';


const fields = Map({
  name: "string",
  id: "number",
  date: "time",
  boolean: "boolean"
});

const constraints = Map({
  string: [
    "Contains",
    "StartsWith",
    "EndsWith",
    "EqualTo",
    "NotEqualTo",
    "GreaterThan",
    "LessThan",
    "GreaterThanOrEqualTo",
    "LessThanOrEqualTo",
    "IsEmpty",
    "NotIsEmpty",
    "IsNull",
    "NotIsNull"
  ],
  number: [
    "EqualTo",
    "NotEqualTo",
    "GreaterThan",
    "LessThan",
    "GreaterThanOrEqualTo",
    "LessThanOrEqualTo",
    "IsNull",
    "NotIsNull"
  ],
  time: [
    "EqualTo",
    "NotEqualTo",
    "GreaterThan",
    "LessThan",
    "GreaterThanOrEqualTo",
    "LessThanOrEqualTo",
    "IsNull",
    "NotIsNull"
  ],
  boolean: [],
  default: [
    "EqualTo",
    "NotEqualTo",
    "GreaterThan",
    "LessThan",
    "GreaterThanOrEqualTo",
    "LessThanOrEqualTo",
    "IsNull",
    "NotIsNull"
  ]
});


const TableView = (props) => {
  const {
    data,
    addPerson,
    setPersonName,
    deletePerson,
    editPerson,
    confirmEdit,
    makeMainQuery,

  } = tableController();

  const { filterData } = props
  const columns = [
    {
      title: 'objectId',
      dataIndex: 'objectId',
      key: 'objectId',
    },
    {
      title: 'Name',
      dataIndex: 'name',
      key: 'name',
      render: text => <a>{text}</a>,
    },
    {
      title: 'createdAt',
      dataIndex: 'createdAt',
      key: 'createdAt',
    },
    {
      title: 'updatedAt',
      dataIndex: 'updatedAt',
      key: 'updatedAt',
    },
    {
      title: 'Action',
      key: 'action',
      render: (text, record) => (
        <span>
          <Icon type="delete" key="delete" onClick={() => deletePerson(record.key)}></Icon>
          <Divider type="vertical" />
          <Icon type="edit" key="edit" onClick={() => editPerson(record.key)}></Icon>
        </span>
      ),
    },
  ];


  let tmpdata;
  let dataSource;
  const ClouseQuery = Parse.Object.extend("ClouseQuery");

  return (
    <div style={{ padding: 24 }}>
      <Button onClick={() => makeMainQuery(data.tmpdata)}>Search</Button>
      <OprandView
        makeMainQuery={makeMainQuery}
        filterData={filterData}
        constraints={constraints}
        fields={fields}
      />

      <Row>
        <Col span={8}>
          {data.editing &&
            <h1>Edit Mode</h1>
          }
          <Input placeholder="name" value={data.personName} onChange={(e) => setPersonName(e.target.value)} />
        </Col>
        <Col span={8}>
          {!data.editing &&
            <Button onClick={() => addPerson()}>add</Button>
          }
          {data.editing &&
            <Button onClick={() => confirmEdit()}>confirm edit</Button>
          }
        </Col>
      </Row>
      <Row>
        <Col span={12}>
          <Table style={{ padding: 24 }} columns={columns} dataSource={data.personsList} />
        </Col>
      </Row>
    </div>
  );
};

export default TableView;
